		String hexParamtersFileJPF = "2112100041004a6f6245786563506172616d7300211700004e004a6f6252756e6e696e67424d554948776e640023050026045a0002212100004e004a6f6252756e6e696e67424d746f4f50436f6e6e46696c654e616d650023a503" + hexConDat1 + "" + hexNameJob + "000002211600004e004a6f6252756e6e696e674a6f6254797065002305000100000002212100004e004a6f6252756e6e696e674f50746f424d436f6e6e46696c654e616d650023a503" + hexConDat2 + "" + hexNameLog + "" + hexPathOCR + "000002020202";
